mapper.select:
select dp.title,dp.describes,drp.browse_count browseCount from doctor_reply_patient drp left join doctor_patient dp on drp.patient_id=dp.id where 1=1
and drp.doctor_id=#{doctorId}
group by drp.patient_id
limit #{pageNo},#{pageSize}

select d.name,d.head_img headImg,d.hospital,d.offices,d.professional,d.skilled,drp.doctor_id doctorId from doctor_reply_patient drp left join doctors d on d.id=drp.doctor_id where 1=1
and drp.patient_id=#{patientId}
GROUP BY doctor_id

select doctor_id doctorId,patient_id patientId,reply_content replyContent,addtime from doctor_reply_patient where 1=1
	
	and patient_id=#{patientId}
	
	
	and doctor_id=#{doctorId}
